import React from "react"
import { Flex, Text, Tag } from "@chakra-ui/react";

const TodoComponent = ({todoTask, urgency}) => 
{
    return (
        <Flex w="100%" bgColor="white" borderRadius="10" borderColor={"gray.500"} borderWidth="1px" p="5" mt="5">
            <Flex>
                <Text fontSize={"18px"} fontWeight="bold">{todoTask}</Text>
                <Tag 
                    ml="5"
                    colorScheme={(urgency === "Urgent" ? "red": (urgency === "Important" ? "yellow" : "cyan"))}
                >
                    <Text fontWeight="bold">{urgency}</Text>
                </Tag>
            </Flex>
            <Flex>

            </Flex>
        </Flex>
    )
}

export default TodoComponent;
